<?phpclass Criteria_model extends MY_Model {	protected $_table = 'qm.criteria';	protected $primary_key = 'criteria_id';	function join_qm_and_criteria($qm_id)	{		$this->db->select('*');		$this->db->from('qm_and_criteria');		$this->db->join('qm_form', 'qm_and_criteria.qm_id = qm_form.qm_id');		$this->db->join('criteria', 'qm_and_criteria.criteria_id = criteria.criteria_id');		$this->db->where('qm_form.qm_id', $qm_id);		$this->db->order_by('criteria.criteria_title', 'asc');		$query = $this->db->get();		return $query->result();	}			function sort_tenant($tenant_id)	{		$this->db->where('criteria.tenant_id', $tenant_id);		return $this;	}}?>