<?phpclass Criteriaandquestion_model extends MY_Model {	protected $_table = 'qm.criteria_and_question';	//protected $primary_key = 'criteria_id';		function get_criteriaandquestion($qm_id)	{		$this->db->select('*');		$this->db->from('criteria_and_question');		$this->db->where('criteria_and_question.qm_id', $qm_id);		$query = $this->db->get();		return $query->result();	}}?>