<?phpclass Qmandcriteria_model extends MY_Model {	protected $_table = 'qm.qm_and_criteria';	//protected $primary_key = 'criteria_id';	function count_criteria_rate($qm_id)	{		$this->db->select('*');		$this->db->from('qm_and_criteria');		$this->db->join('criteria', 'qm_and_criteria.criteria_id = criteria.criteria_id');		$this->db->where('qm_and_criteria.qm_id', $qm_id);		$query = $this->db->get();		return $query->result();	}	function edit_count_criteria_rate($qm_id,$criteria_id)	{		$this->db->select('*');		$this->db->from('qm_and_criteria');		$this->db->join('criteria', 'qm_and_criteria.criteria_id = criteria.criteria_id');		$this->db->where('qm_and_criteria.criteria_id !=', $criteria_id);		$this->db->where('qm_and_criteria.qm_id', $qm_id);		$query = $this->db->get();		return $query->result();	}	function get_qmandcriteria($qm_id)	{		$this->db->select('*');		$this->db->from('qm_and_criteria');		$this->db->where('qm_and_criteria.qm_id', $qm_id);		$query = $this->db->get();		return $query->result();	}}?>