<?phpclass Recqm_model extends MY_Model {	protected $_table = 'qm.recordings_and_qm';	//protected $primary_key = 'record_id';	function join_qm_recording($record_id,$qm_id,$userid,$recording_type)	{				$this->load->database('callcenter', TRUE);						$this->db->join('qm.qm_form', 'recordings_and_qm.qm_id = qm_form.qm_id');		$this->db->join('qm.recordings', 'recordings_and_qm.record_id = recordings.record_id');		if($recording_type == 1){			$this->load->database('reportcallcenter', TRUE);			$this->db->join('reportcallcenter.callcontactsdetails', 'recordings.unique_id = callcontactsdetails.callid');		}					$this->db->join('callcenter.users', 'recordings.userid = users.userid');		$this->db->join('qm.qm_and_criteria', 'qm_form.qm_id = qm_and_criteria.qm_id');		$this->db->join('qm.criteria', 'qm_and_criteria.criteria_id = criteria.criteria_id');		$this->db->join('qm.criteria_and_question', 'criteria.criteria_id = criteria_and_question.criteria_id');		$this->db->join('qm.question', 'criteria_and_question.question_id = question.question_id');		//$this->db->group_by("qm_form.qm_title");		$this->db->where('qm.recordings.record_id', $record_id);		$this->db->where('qm.qm_form.qm_id', $qm_id);		$this->db->where('qm.criteria_and_question.qm_id', $qm_id);		//$this->db->where('reportcallcenter.callcontactsdetails.userid', $userid);		$this->db->where('callcenter.users.userid', $userid);		$this->db->order_by('qm.criteria.criteria_title', 'asc');		return $this;	}	function get_record_id($record_id)	{		$this->db->where('qm.recordings_and_qm.record_id', $record_id);		return $this;	}}?>